def rule_1446008377862615040(inp_data):
    results = []
    try:
        names = inp_data.get('invoice', {}).get('vendor_address', {}).get('name', [])
        if not isinstance(names, list):
            names = [names]
        element_location = inp_data.get('invoice', {}).get('vendor_address', {}).get('element_location', '')
        for name in names:
            if not name:
                results.append({'invoice/vendor_address/name': '', 'identifier1_location': element_location, 'result': 'FAIL'})
                continue
            if all(char.isalpha() or char.isspace() for char in name):
                results.append({'invoice/vendor_address/name': name.replace(' ', ''), 'identifier1_location': element_location, 'result': 'PASS'})
            else:
                results.append({'invoice/vendor_address/name': name.replace(' ', ''), 'identifier1_location': element_location, 'result': 'FAIL'})
    except:
        results.append({'invoice/vendor_address/name': '', 'identifier1_location': '', 'result': 'FAIL'})
    return results
