def rule_1438135805854887936(inp_data):
    results = []
    try:
        invoices = inp_data.get('data', [])
        for invoice_data in invoices:
            invoice = invoice_data.get('invoice', {})
            invoice_numbers = invoice.get('invoice_number', [])
            if not isinstance(invoice_numbers, list):
                invoice_numbers = [invoice_numbers]
            for invoice_number in invoice_numbers:
                result = {}
                result['data/invoice/invoice_number'] = invoice_number if invoice_number else ''
                result['identifier1_location'] = invoice.get('element_location', '')
                if invoice_number.isdigit():
                    result['result'] = 'PASS'
                else:
                    result['result'] = 'FAIL'
                results.append(result)
    except:
        results.append({'data/invoice/invoice_number': '', 'identifier1_location': '', 'result': 'FAIL'})
    return results
